Vue.component("app-title",{props:{title:{type:String,default:"Optinomic"},subtitle:String},created(){this.$store.dispatch("getSurveyResponses"),this.$store.dispatch("getApps")},computed:{readme_html(){return null===this.$store.state.current_app?"<h1>Loading</h1>":this.$store.state.current_app.module.readme.html}},template:'\n        <v-expansion-panels flat light>\n            <v-expansion-panel>\n                <v-expansion-panel-header>\n                    <h1 v-html="title" class="display-2 font-weight-thin"></h1>\n                    <p v-html="subtitle"></p>\n                </v-expansion-panel-header>\n                <v-expansion-panel-content>\n                    <v-subheader>Readme</v-subheader>\n                    <div v-html="readme_html"></div>\n                </v-expansion-panel-content>\n            </v-expansion-panel>\n        </v-expansion-panels>\n    '});