Vue.component("app-title",{props:{title:{type:String,default:"Optinomic"},subtitle:{type:String,default:"App"}},created(){this.$store.dispatch("getApps")},computed:{readme_html(){return null===this.$store.state.current_app?null:this.$store.state.current_app.module.readme.html}},template:'\n        <div style="margin-bottom:24px;">\n            <h1 v-html="title" class="display-1 font-weight-thin"></h1>\n            <p v-html="subtitle" style="margin-left:3px"></p>\n            <v-divider></v-divider>\n \n            <v-expansion-panels flat light tile v-if="readme_html === null">\n                <v-expansion-panel disabled>\n                    <v-expansion-panel-header style="padding:0">\n                        <v-skeleton-loader\n                            class=""\n                            min-width="200"\n                            max-width="550"\n                            type="heading"\n                        ></v-skeleton-loader>\n                    </v-expansion-panel-header>\n                </v-expansion-panel>\n            </v-expansion-panels>\n\n            <v-expansion-panels flat light tile v-if="readme_html !== null">\n                <v-expansion-panel>\n                    <v-expansion-panel-header style="padding:0">\n                        <h2 class="font-weight-light">Dokumentation (Readme)</h2>\n                    </v-expansion-panel-header>\n                    <v-expansion-panel-content>\n                        <div v-html="readme_html"></div>\n                    </v-expansion-panel-content>\n                </v-expansion-panel>\n            </v-expansion-panels>\n            <v-divider></v-divider>\n        </div>\n    '});