Vue.component("optinomic-clipboard-text",{props:{text:{type:String,default:""}},data:()=>({snackbar:!1,alert_text:"",alert_color:"error",timeout:2e3}),methods:{copyClipboard(){try{let textToCopy=document.querySelector("#clipboard-text");textToCopy.setAttribute("type","text"),textToCopy.select(),document.execCommand("copy")?(this.alert_text="✓ Erfolgreich in die Zwischenablage kopiert.",this.alert_color="success"):this.alert_text="Oops, der Text konnte nicht in die Zwischenablage kopiert werden.",textToCopy.setAttribute("type","hidden"),window.getSelection().removeAllRanges()}catch(err){this.alert_text="Oops, kopieren in die Zwischenablage ist nicht möglich."}this.snackbar=!0}},computed:{},template:'\n        <div>\n            <v-tooltip left>\n                <template v-slot:activator="{ on }">\n                    <span v-on="on" style="float:right;">\n                        <v-btn @click="copyClipboard" icon color="indigo">\n                            <v-icon>mdi-content-copy</v-icon>\n                        </v-btn>\n                    </span>\n                </template>\n                <span>Kopiere in die Zwischenablage.</span>\n            </v-tooltip>\n\n            <p class="text--primary body-1" v-text="text">optinomic-clipboard-text</p>\n\n            <input type="hidden" id="clipboard-text" :value="text">\n\n            <v-snackbar v-model="snackbar" :timeout="timeout" :color="alert_color">\n                <span v-text="alert_text"></span>\n            </v-snackbar>\n        </div>\n    '});